create table events
(
    eventID          int auto_increment
        primary key,
    hostUserID       int                       null,
    startDate        datetime                  null,
    endDate          datetime                  null,
    eventDescription varchar(500) charset utf8 null,
    note             varchar(50) charset utf8  null,
    location         varchar(100) charset utf8 null,
    category         varchar(30) charset utf8  null,
    title            varchar(100) charset utf8 null,
    department       varchar(100)              null,
    imageUrl         varchar(300) charset utf8 null,
    freeFood         tinyint(1) default 0      null,
    merchandise      tinyint(1) default 0      null,
    address          varchar(100)              null,
    city             varchar(50)               null,
    state            varchar(20)               null
);


